import React, { useEffect, useState } from 'react';
import { View, StyleSheet, TextInput, FlatList, Text, ScrollView } from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import Header from '../../components/Header';

const Academy = () => {

  const [data, setData] = useState([]);
  const [newData, setNewData] = useState(null);

  useEffect(() => {
    const getData = async () => {
      const response = await fetch('https://jsonplaceholder.typicode.com/posts');
      const json = await response.json();
      setData(json);
    }
    getData();
  }, []);

  const renderItem = (item) => {
    const { title, body } = item.item;
    return (
      <View>
        <Text>{ title }</Text>
        <Text>{ body }</Text>
      </View>
    )
  }

  const searchTitle = (value) => {
    const newRecord = data.filter(item => item.title.includes(value));
    if (value === '') {
      console.log(data);
      setNewData(data);
    } else {
      setNewData(newRecord);
    }
  }

  return (
    <View style={styles.container}>
      <Header title="Academy header" />
      <ScrollView>
        <TextInput onChangeText={searchTitle} placeholder='Filter'></TextInput>
          <FlatList
              data={newData } 
              renderItem={renderItem}
              keyExtractor={item => item.id}
          />
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});

export default Academy;
